import { Component, inject } from '@angular/core';
import { BreakpointObserver, Breakpoints } from '@angular/cdk/layout';
import { Observable } from 'rxjs';
import { map, shareReplay } from 'rxjs/operators';

import { DeviceTableComponent } from '../device-table/device-table.component';
import { DeviceMapComponent } from '../device-map/device-map.component';

export interface DeviceElement {
  endDeviceId: number;
  appId: number;
  packetLoss: number[];
  gatewayId: string;
  time: string[];
  batteryStat: number[];
}

const ELEMENT_DATA: DeviceElement[] = [
  // Your data
];

@Component({
  selector: 'app-dashboard',
  templateUrl: './dashboard.component.html',
  styleUrls: ['./dashboard.component.css'],
})
export class DashboardComponent {
  private breakpointObserver = inject(BreakpointObserver);

  isHandset$: Observable<boolean> = this.breakpointObserver
    .observe(Breakpoints.Handset)
    .pipe(
      map((result) => result.matches),
      shareReplay()
    );

  dataSource: DeviceElement[] = ELEMENT_DATA;
}
<p>dashboard works!</p>
<!--This is the side nave container, and it wraps everything because otherwaise the side bar wouldn't be able to pop out over page-->
<mat-sidenav-container class="sidenav-container">
    <!-- Can add ngclass that defines a hidden which only shows the hamburger side nav if the user is on handheld mode, mode makes the side bar either push or appear over main page -->
    <mat-sidenav
    #drawer
    class="sidenav"
    fixedInViewport="false"
    [attr.role]="(isHandset$ | async) ? 'dialog' : 'navigation'"
    [mode]="'over'"
    [opened]="false"
    >
    <mat-toolbar>Organizations</mat-toolbar>
    <button mat-raised-button color="accent">Add</button>
    <button mat-raised-button color="warn">Remove</button>
    <mat-nav-list>
        <a mat-list-item href="#">Organization 1</a>
        <a mat-list-item href="#">Organization 2</a>
        <a mat-list-item href="#">Organization 3</a>
    </mat-nav-list>
    </mat-sidenav>
