import { Component, Input, OnInit } from '@angular/core';
import { DeviceElement } from '../app.component';
import { ChartService } from '../chart.service';

@Component({
  selector: 'app-device-stats',
  templateUrl: './device-stats.component.html',
  styleUrls: ['./device-stats.component.css'],
  providers: [ChartService,]
})
export class DeviceStatsComponent implements OnInit {
  @Input() Devicelist!: DeviceElement [];

  showSpinner: boolean = false;
  tempDevice!: DeviceElement;

  constructor(private chart: ChartService){}

  ngOnInit(): void {
    this.chart.createPktLossChart(this.Devicelist[0]);
  }

  viewDeviceHealth(row: DeviceElement)
  {
    this.tempDevice = row;
    this.chart.updateChartData(row);
  }

  viewDevicePktloss()
  {
    this.chart.updateChartData(this.tempDevice);
  }

  viewDeviceBattery()
  {
    this.chart.toBarChart(this.tempDevice);
  }
  loadSpinner() {
    this.showSpinner = true;
    setTimeout(() =>{this.showSpinner = false}, 250)
  }


}
<p>device-stats works!</p>
<div>
    <mat-card>
        <mat-card-title>Device Data Devices</mat-card-title>
        <mat-card-header>Device Statistic</mat-card-header>
        <mat-card-content>
            <form>
                <table>
                    <tr>
                        <td>
                            <mat-form-field class="example-full-width">
                            <mat-label>Start Date-Time</mat-label>
                            <input matInput placeholder="Ex. 11/17/23 10:05">
                            </mat-form-field>
                        </td>
                        <td>
                            <mat-form-field class="example-full-width">
                            <mat-label>End Date-Time</mat-label>
                            <input matInput placeholder="Ex. 11/19/23 10:05">
                            </mat-form-field>
                        </td>
                    </tr>
                </table>          
            </form>
            <div class="grid-container">
                <div class="grid-child">
                    <table mat-table [dataSource]="Devicelist" class="mat-elevation-z4">
                        <ng-container matColumnDef="endDeviceId">
                            <th mat-header-cell *matHeaderCellDef> Device EUI </th>
                            <td mat-cell *matCellDef="let element"> {{element.endDeviceId}} </td>
                        </ng-container>

                        <ng-container matColumnDef="gatewayId">
                            <th mat-header-cell *matHeaderCellDef> Gateway EUI </th>
                            <td mat-cell *matCellDef="let element"> {{element.gatewayId}} </td>
                        </ng-container>

                        <ng-container matColumnDef="packetLoss">
                            <th mat-header-cell *matHeaderCellDef> Packet Loss </th>
                            <td mat-cell *matCellDef="let element"> {{element.packetLoss[0]/100 | percent: '1.1'}}</td>
                        </ng-container>

                        <tr mat-header-row *matHeaderRowDef="['endDeviceId', 'gatewayId', 'packetLoss']"></tr>
                        <tr (click)="loadSpinner()" (click)="viewDeviceHealth(row)" mat-row *matRowDef="let row; columns: ['endDeviceId', 'gatewayId', 'packetLoss']"></tr>

                    </table>
                </div>

                <div class="grid-child" class="mat-elevation-z4">
                    <canvas id="myChart"></canvas>
                </div>

            </div>
        </mat-card-content>

        <mat-card-actions>
            <button mat-raised-button (click)="loadSpinner()" (click)="viewDevicePktloss()">Add Device</button>
            <button mat-raised-button (click)="loadSpinner()" >View Packet Loss</button>
            <button mat-raised-button (click)="loadSpinner()">View RSSI</button>
            <button mat-raised-button (click)="loadSpinner()">View SNR</button>
            <button mat-raised-button (click)="loadSpinner()" (click)="viewDeviceBattery()">View Battery</button>
            <button mat-raised-button (click)="loadSpinner()">Export Data</button>
            <mat-spinner *ngIf="showSpinner"></mat-spinner>
        </mat-card-actions>

    </mat-card>
</div>
